/** @format */

import React, { useState } from "react";
import "../../styles/find-car-form.css"; // Giữ lại các lớp CSS cũ của bạn
import { Button } from "reactstrap";

const FindCarTable = () => {
  const [searchParams, setSearchParams] = useState({
    pickupAddress: "",
    dropoffAddress: "",
    rentalDate: "",
    rentalTime: "",
    carType: "",
  });

  const handleChange = (e) => {
    setSearchParams({
      ...searchParams,
      [e.target.name]: e.target.value,
    });
  };

  const handleSearch = () => {
    console.log("Search Params:", searchParams);
    // Perform the search based on searchParams
  };

  return (
    <div className="search-table-container">
      {/* Bảng tìm kiếm */}
      <table className="min-w-full table-auto border-collapse">
        <thead>
          <tr className="border-b">
            <th className="px-4 py-2 text-left">Địa chỉ nhận xe</th>
            <th className="px-4 py-2 text-left">Địa chỉ trả xe</th>
            <th className="px-4 py-2 text-left">Ngày thuê</th>
            <th className="px-4 py-2 text-left">Giờ thuê</th>
            <th className="px-4 py-2 text-left">Loại xe</th>
            <th className="px-4 py-2 text-left">Tìm kiếm</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td className="px-4 py-2">
              <input
                type="text"
                name="pickupAddress"
                value={searchParams.pickupAddress}
                onChange={handleChange}
                placeholder="Nhập địa chỉ nhận xe"
                className="w-full px-2 py-1 border border-gray-300 rounded"
              />
            </td>
            <td className="px-4 py-2">
              <input
                type="text"
                name="dropoffAddress"
                value={searchParams.dropoffAddress}
                onChange={handleChange}
                placeholder="Nhập địa chỉ trả xe"
                className="w-full px-2 py-1 border border-gray-300 rounded"
              />
            </td>
            <td className="px-4 py-2">
              <input
                type="date"
                name="rentalDate"
                value={searchParams.rentalDate}
                onChange={handleChange}
                className="w-full px-2 py-1 border border-gray-300 rounded"
              />
            </td>
            <td className="px-4 py-2">
              <input
                type="time"
                name="rentalTime"
                value={searchParams.rentalTime}
                onChange={handleChange}
                className="w-full px-2 py-1 border border-gray-300 rounded"
              />
            </td>
            <td className="px-4 py-2">
              <select
                name="carType"
                value={searchParams.carType}
                onChange={handleChange}
                className="w-full px-2 py-1 border border-gray-300 rounded">
                <option value="">Chọn loại xe</option>
                <option value="sedan">Sedan</option>
                <option value="suv">SUV</option>
                <option value="mpv">MPV</option>
              </select>
            </td>
            <td className="px-4 py-2">
              <Button
                color="primary"
                onClick={handleSearch}
                className="find__car-btn" // Giữ lại class cũ của bạn
              >
                Tìm xe
              </Button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default FindCarTable;
