/** @format */

import React from "react";
import "../../styles/find-car-form.css";
import { Form, FormGroup, Label, Input, Button } from "reactstrap";

const FindCarForm = () => {
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form submitted!");
  };

  return (
    <Form className="form" onSubmit={handleSubmit}>
      <div className="d-flex align-items-center justify-content-between flex-wrap">
        {/* From Address */}
        <FormGroup className="form__group">
          <Label for="fromAddress">From Address</Label>
          <Input
            id="fromAddress"
            type="text"
            placeholder="Enter pickup location"
            required
          />
        </FormGroup>

        {/* To Address */}
        <FormGroup className="form__group">
          <Label for="toAddress">To Address</Label>
          <Input
            id="toAddress"
            type="text"
            placeholder="Enter drop-off location"
            required
          />
        </FormGroup>

        {/* Journey Date */}
        <FormGroup className="form__group">
          <Label for="journeyDate">Journey Date</Label>
          <Input id="journeyDate" type="date" required />
        </FormGroup>

        {/* Journey Time */}
        <FormGroup className="form__group">
          <Label for="journeyTime">Journey Time</Label>
          <Input id="journeyTime" type="time" required />
        </FormGroup>

        {/* Car Type */}
        <FormGroup className="select__group">
          <Label for="carType">Car Type</Label>
          <Input id="carType" type="select" required>
            <option value="sedan">Sedan</option>
            <option value="suv">SUV</option>
            <option value="mpv">MPV</option>
          </Input>
        </FormGroup>

        {/* Submit Button */}
        <FormGroup className="form__group">
          <Button className="btn find__car-btn" type="submit">
            Find Car
          </Button>
        </FormGroup>
      </div>
    </Form>
  );
};

export default FindCarForm;
